- (WWNavigationVC *)nav {
    if (_nav == nil) {
        _nav = [[WWNavigationVC alloc]initWithFrame:CGRectMake(0, 20, KWidth, 44)];
        _nav.backBtn.hidden = NO;
        [_nav.backBtn addTarget:self action:@selector(backClick) forControlEvents:UIControlEventTouchUpInside];
        _nav.navTitle.text = @"使用协议";
    }
    return _nav;
}

- (YYTextView *)regisView {
    if (_regisView == nil) {
        _regisView = [[YYTextView alloc]init];
        _regisView.font = [UIFont fontWithName:kFont_SemiBold size:14*screenRate];
        _regisView.textColor = RGBCOLOR(0xffffff);
        _regisView.editable = NO;
        [_regisView endEditing:NO];
    }
    return _regisView;
}
